  const beaniePinAnimation = {
    initial: { scale: 1 },
    animate: { scale: [1, 1.2, 1], rotate: [0, 6, -6, 0] },
    transition: { repeat: Infinity, duration: 2.5, ease: "easeInOut" },
  };

  const characterWaveAnimation = {
