            onClick={() => setActiveTab("newPost")}
          >
            New Post
          </TabsTrigger>
          <TabsTrigger
            value="profile"
            className="rounded-xl font-bold"
            onClick={() => setActiveTab("profile")}
          >
            Profile
          </TabsTrigger>
        </TabsList>

        {/* Feed Tab */}
        <TabsContent value="feed" className="mt-4 space-y-4">
          {posts.map((post) => (
            <Card
              key={post.id}
              className="p-4 shadow-md flex flex-col gap-2 border border-slate-100"
